mapper.update:
UPDATE player

	name=#{name},
	telephone=#{telephone},
	email=#{email},
	pwd=#{pwd}

WHERE code=#{code}

mapper.select:
SELECT g.id id,r.name rname,b.name bname,g.startTime startTime,(g.endTime-g.startTime) time,DECODE(g.winId,r.id,r.name,b.name) winName
FROM game g JOIN player r ON g.redId=r.id JOIN player b ON g.blackId=b.id

	(g.redId=#{id} or g.blackId=#{id})
	and g.winId=#{id}
	and g.winId<>#{id}
	and g.redId=#{id}
	and g.blackId=#{id}
	and g.startTime>#{begin}
	and g.startTime<#{end}
